function easeInOut(n,e,a,t,o){return Math.ceil(n+Math.pow(1/a*t,o)*(e-n))}function doBGFade(n,e,a,t,o,r,c){n.bgFadeInt&&window.clearInterval(n.bgFadeInt);var d=0;n.bgFadeInt=window.setInterval(function(){n.css("backgroundColor","rgb("+easeInOut(e[0],a[0],o,d,c)+","+easeInOut(e[1],a[1],o,d,c)+","+easeInOut(e[2],a[2],o,d,c)+")"),(d+=1)>o&&(n.css("backgroundcolor",t),window.clearInterval(n.bgFadeInt))},r)}