function mktoggle(t,a){return'<i class="'+t+" fa fa-toggle-"+(a?"on":"off")+'"></i>'}function tick(t){return mktoggle("",t)}function toggle(t){t.toggleClass("fa-toggle-off").toggleClass("fa-toggle-on")}function dotoggle(t,a,e,o){if(t.preventDefault(),t.stopPropagation(),a.hasClass("htick")){var n=a.next();n.val(1==n.val()?0:1),toggle(a)}else{var l=a.parent().parent();$.post(base+"/ajax/toggle/",{field:o,bean:e,id:l.data("id")},function(t){toggle(a)})}}function dodelbean(t,a,e){t.preventDefault(),t.stopPropagation(),bootbox.confirm("Are you sure you you want to delete this "+e+"?",function(t){if(t){var o=$(a).parent().parent();$.ajax(base+"/ajax/bean/"+e+"/"+o.data("id")+"/",{method:"DELETE"}).done(function(){o.css("background-color","yellow").fadeOut(1500,function(){o.remove()})}).fail(function(){bootbox.alert("Delete failed")})}})}function mkinline(t,a,e,o,n){return'<a href="#" class="ppedit" data-name="'+a+'" data-type="'+t+'" data-pk="'+o+'" data-url="'+base+'/ajax.php" data-title="'+e+'">'+n+"</a>"}