function mktoggle(a,t){return'<i class="'+a+" fas fa-toggle-"+(t?"on":"off")+'"></i>'}function tick(a){return mktoggle("",a)}function toggle(a){a.toggleClass("fa-toggle-off").toggleClass("fa-toggle-on")}function dotoggle(a,t,e,o){if(a.preventDefault(),a.stopPropagation(),!t.hasClass("fadis"))if(t.hasClass("htick")){var n=t.next();n.val(1==n.val()?0:1),toggle(t)}else{var l=t.parent().parent();$.post(base+"/ajax/toggle/",{field:o,bean:e,id:l.data("id")},function(a){toggle(t)})}}function dodelbean(a,t,e){a.preventDefault(),a.stopPropagation(),bootbox.confirm("Are you sure you you want to delete this "+e+"?",function(a){if(a){var o=$(t).parent().parent();$.ajax(base+"/ajax/bean/"+e+"/"+o.data("id")+"/",{method:"DELETE"}).done(function(){o.css("background-color","yellow").fadeOut(1500,function(){o.remove()})}).fail(function(){bootbox.alert("Delete failed")})}})}function mkinline(a,t,e,o,n){return'<a href="#" class="ppedit" data-name="'+t+'" data-type="'+a+'" data-pk="'+o+'" data-url="'+base+'/ajax.php" data-title="'+e+'">'+n+"</a>"}